mapper.select:
select

from t_token
where user_ip = #{userIp,jdbcType=VARCHAR}
and user_id = #{userId,jdbcType=CHAR}

mapper.delete:
delete from t_token
where user_ip = #{userIp,jdbcType=VARCHAR}
  and user_id = #{userId,jdbcType=CHAR}

mapper.insert:
insert into t_token (token, secret_key, user_ip,
  user_id, add_time, upd_time
  )
values (#{token,jdbcType=CHAR}, #{secretKey,jdbcType=CHAR}, #{userIp,jdbcType=VARCHAR},
  #{userId,jdbcType=CHAR}, #{addTime,jdbcType=TIMESTAMP}, #{updTime,jdbcType=TIMESTAMP}
  )
